mapper.select:
SELECT COUNT(*)
FROM ACCOUNTING
WHERE status ='ACTIVE'



            AND created_date >= #{startDate}

        AND created_date <= #{endDate}


        AND created_date >= #{startDate}


        AND created_date <= #{endDate}

mapper.insert:
INSERT
INTO ACCOUNTING (id,created_date,spending,remain, user_id, bill, title, status, note)
VALUES (#{id}, #{createdDate},#{expense}, #{remain},#{user.id}, #{bill}, #{title}, #{status}, #{note});

update.foreach:
UPDATE ACCOUNTING
SET
spending = #{record.expense},
remain = #{record.remain}
WHERE id = #{record.id}

mapper.select:
SELECT a.*, u.fullname
FROM ACCOUNTING a
INNER JOIN
USER u
ON a.user_id = u.id
WHERE a.status ='ACTIVE'



              AND a.created_date >= #{startDate}

          AND a.created_date <= #{endDate}


          AND a.created_date >= #{startDate}


          AND a.created_date <= #{endDate}


ORDER BY a.created_date ASC

SELECT SUM(CASE WHEN spending > 0 THEN spending ELSE 0 END) AS totalRevenue,
       SUM(CASE WHEN spending < 0 THEN spending ELSE 0 END) AS totalExpense
FROM ACCOUNTING
WHERE status ='ACTIVE'



                AND created_date >= #{startDate}

            AND created_date <= #{endDate}


            AND created_date >= #{startDate}


            AND created_date <= #{endDate}

mapper.update:
UPDATE ACCOUNTING
SET status = 'DISABLE'
WHERE id =#{id}
