update.foreach:
UPDATE MANAGEMENT_TIME_DAY
SET ONE_THING_CALENDAR = #{day.oneThingCalendar}
WHERE ID = #{day.id}

mapper.update:
UPDATE MANAGEMENT_TIME_DAY
SET ONE_THING_CALENDAR = null
WHERE USER_ID = #{userId}
AND DAY IN

    #{day}

mapper.select:
SELECT *
FROM MANAGEMENT_TIME_DAY
WHERE WEEKLY_CODE LIKE CONCAT(#{weeklyCode}, '%');
