mapper.select:
SELECT YEAR(t.WORK_DATE) AS year, COUNT(*) AS workdays
FROM TIMESHEETS t
INNER JOIN USER u ON t.TIMESHEETS_CODE = u.TIMESHEETS_CODE
WHERE u.ID = #{userID}
GROUP BY year
ORDER BY year ASC;

SELECT MONTH(t.WORK_DATE) AS month, COUNT(*) AS workdays
FROM TIMESHEETS t
INNER JOIN USER u ON t.TIMESHEETS_CODE = u.TIMESHEETS_CODE
WHERE u.ID = #{userID} AND YEAR(t.WORK_DATE) = #{year}
GROUP BY month
ORDER BY month ASC;
